namespace MageINIC\BannerSlider\Block\Adminhtml\Slider\Grid\Renderer;

use Magento\Backend\Block\Context;
use Magento\Backend\Block\Widget\Grid\Column\Renderer\AbstractRenderer;
use Magento\Framework\DataObject;

/**
 * BannerSlider Action Class
 */
class Action extends AbstractRenderer
{
    /**
     * @var Action\UrlBuilder
     */
    protected Action\UrlBuilder $actionUrlBuilder;

    /**
     * @param Context           $context
     * @param Action\UrlBuilder $actionUrlBuilder
     * @param array             $data
     */
    public function __construct(
        Context $context,
        Action\UrlBuilder $actionUrlBuilder,
        array $data = []
    ) {
        $this->actionUrlBuilder = $actionUrlBuilder;
        parent::__construct($context, $data);
    }

    /**
     * Render action
     *
     * @param  DataObject $row
     * @return string
     */
    public function render(DataObject $row): string
    {
        $href = $this->actionUrlBuilder->getUrl(
            $row->getIdentifier(),
            $row->getData('_first_store_id'),
            $row->getStoreCode()
        );
        return '<a href="' . $href . '" target="_blank">' . __('Preview') . '</a>';
    }
}
