namespace MageINIC\BannerSlider\Controller\Adminhtml\Banner;

use Exception;
use Magento\Backend\App\Action;
use Magento\Backend\App\Action\Context;
use Magento\Framework\App\Action\HttpPostActionInterface;
use Magento\Framework\App\ResponseInterface;
use Magento\Framework\Controller\Result\Json;
use Magento\Framework\Controller\ResultFactory;
use MageINIC\BannerSlider\Model\MediaUploader;
use Magento\Framework\Controller\ResultInterface;

/**
 * Upload Media Video Controller.
 */
class VideoUploader extends Action implements HttpPostActionInterface
{
    /**
     * Authorization level of a basic admin session
     *
     * @see _isAllowed()
     */
    public const ADMIN_RESOURCE = 'MageINIC_BannerSlider::banner_save';

    /**
     * @var MediaUploader
     */
    public MediaUploader $mediaUploader;

    /**
     * Upload constructor.
     *
     * @param Context $context
     * @param MediaUploader $mediaUploader
     */
    public function __construct(
        Context       $context,
        MediaUploader $mediaUploader
    ) {
        parent::__construct($context);
        $this->mediaUploader = $mediaUploader;
    }

    /**
     * @return ResponseInterface|Json|ResultInterface
     */
    public function execute()
    {
        try {
            $result = $this->mediaUploader->saveFileToTmpDir('video_media');
            $result['cookie'] = [
                'name' => $this->_getSession()->getName(),
                'value' => $this->_getSession()->getSessionId(),
                'lifetime' => $this->_getSession()->getCookieLifetime(),
                'path' => $this->_getSession()->getCookiePath(),
                'domain' => $this->_getSession()->getCookieDomain(),];
        } catch (Exception $e) {
            $result = ['error' => $e->getMessage(), 'errorcode' => $e->getCode()];
        }
        return $this->resultFactory->create(ResultFactory::TYPE_JSON)->setData($result);
    }
}
